mapper.update:
UPDATE
	admin
SET
	admin_email = #{adminEmail}
	,admin_password=#{adminPassword}
	,admin_name=#{adminName}
	,admin_level=#{adminLevel}
	,admin_mobile=#{adminMobile}
WHERE 
	admin_email=#{adminEmail};

mapper.insert:
INSERT INTO admin
	(admin_email
	, admin_password
	, admin_name
	, admin_level
	, admin_mobile
	, admin_join_date
	)VALUES(
	#{adminEmail}
	, #{adminPassword}
	, #{adminName}
	, #{adminLevel}
	, #{adminMobile}
	, CURDATE());
